function getMonthName(month) {
	var m = new Array();
	m[0] ="January";
	m[1] ="February";
	m[2] ="March";
	m[3] ="April";
	m[4] ="May";
	m[5] ="June";
	m[6] ="July";
	m[7] ="August";
	m[8] ="September";
	m[9] ="October";
	m[10] ="November";
	m[11] ="December";
	$wbc('#month_name').html(m[(month-1)]);
	currentMonth = month;

	if((today.getMonth()+1)!=(month)) {
		$wbc('#booking_back_today').fadeIn();
	} else {
		$wbc('#booking_back_today').css("display","none");
	}
}


function showResponse(calendar_id) {
	$wbc('#booking_container_all').parent().prepend('<div id="booking_sfondo" class="booking_modal_sfondo" onclick="hideBookingResponse('+calendar_id+',\'\')"></div>');
	$wbc('#ok_response').attr("href","javascript:hideBookingResponse("+calendar_id+",'');");
	$wbc('#booking_modal_response').fadeIn('slow');
	$wbc('#booking_submit_button').removeAttr("disabled");
}

function showCaptchaError() {
	$wbc('#booking_captcha_error').fadeIn();
	$wbc('#booking_submit_button').removeAttr("disabled");
}

