function updateCalendarSelect(category) {
	$wbc.ajax({
	  url: 'https://www.essentialsafetysolutionsaust.com.au/wp-content/plugins/wp-booking-calendar/public/ajax/getCalendarsList.php?category_id='+category,
	  success: function(data) {
		  arrData = data.split("|");
		  $wbc('#calendar_select_input').html(arrData[0]);
		  $wbc("#calendar_select_input").val($wbc("#calendar_select_input option:first").val());
						var newday= today;

		 getBookingMonthCalendar((newday.getMonth()+1),newday.getFullYear(),arrData[1],'');
	  }
	});
}
function updateCalendar(calendar_id) {
	$wbc.ajax({
	  url: 'https://www.essentialsafetysolutionsaust.com.au/wp-content/plugins/wp-booking-calendar/public/ajax/getCalendar.php?calendar_id='+calendar_id,
	  success: function(data) {

		  				var newday= today;
						 getBookingMonthCalendar((newday.getMonth()+1),newday.getFullYear(),calendar_id,'');
	  }
	});

}
